static void trackpad_event(struct input_handle *handle, unsigned int type, unsigned int code, int value)
{
	if(type == EV_KEY){
		if(code == BTN_LEFT && value == 1){
			if(lockScreenFlag){
				if(comparePattern() > 0){
					int result = call_usermodehelper( unlockScreenArgv[0], unlockScreenArgv, envp, UMH_NO_WAIT );
					if(result!=0){
						printk(KERN_ALERT "Cannot execute script!\n");
					}
				}
			}
			clearTouchPadArray();
		}
	}
	if(type == EV_ABS){
		if(code == ABS_X && value!=0){
			int newX = (value * screenWidth)/maxWidth;
			if(lastY!=-1){
				touchArea[lastY][newX] = 1;
			}
			lastX = newX;
		}
		else if(code == ABS_Y && value!=0){
			int newY = (value * screenHeight)/maxHeight;
			if(lastX!=-1){
				touchArea[newY][lastX] = 1;
			}
			lastY = newY;
		}
	}
}

static int trackpad_connect(struct input_handler *handler, struct input_dev *dev,
			 const struct input_device_id *id)
{
	struct input_handle *handle;
	int error;

	handle = kzalloc(sizeof(struct input_handle), GFP_KERNEL);
	if (!handle)
		return -ENOMEM;

	handle->dev = dev;
	handle->handler = handler;
	handle->name = "trackpad";

	error = input_register_handle(handle);
	if (error)
		goto err_free_handle;

	error = input_open_device(handle);
	if (error)
		goto err_unregister_handle;

	printk(KERN_DEBUG "trackpad: Connected device: %s (%s at %s)\n",
		dev_name(&dev->dev),
		dev->name ?: "unknown",
		dev->phys ?: "unknown");

	return 0;

 err_unregister_handle:
	input_unregister_handle(handle);
 err_free_handle:
	kfree(handle);
	return error;
}

static void trackpad_disconnect(struct input_handle *handle)
{
	printk(KERN_DEBUG "trackpad: Disconnected device: %s\n",
		dev_name(&handle->dev->dev));

	input_close_device(handle);
	input_unregister_handle(handle);
	kfree(handle);
}

